import React, { useState} from "react";
import ProfileCard from './ProfileCard';
import photographer from '../images/photographer.jpg'
import javadev from '../images/java.jpg'

const About = () => {

  const [input, setInput] = useState("");
  let profile = [
    { name: "Sujith UP", profession: "photographer", image: {photographer}, NavLink: "/first" },
    { name: "Satish", profession: "python", image: {javadev},NavLink: "/first" },
    { name: "Nagraj", profession: "java", image: {javadev}, NavLink: "/first" },
  ];

  const handleChange = (e) =>
  {
      e.preventDefault();
      setInput(e.target.value);

  };

    if(input.length > 0){
      profile = profile.filter((i) => {
          return i.profession.toLowerCase().match(input);
      });

    }

    return(
            <div className= "row ">
                <div class="column ">

                <input type="text" className = "form-control col-md-3 mx-auto"
                placeholder = "Search"
                onChange = {handleChange}
                value = {input} />


                {
                    profile.map((player, index) =>
                    {
                        return(
                        //    <div className="container-fluid d-flex justify-content-center">
                                <div className="container-fluid column col-md-4" key={index}>
                                    {/* <ul> <li> */}
                                    <ProfileCard
                                    imgsrc = {javadev}
                                    name = {player.name}
                                    profession = {player.profession}
                                    NavLink = {player.NavLink}
                                />
                                     - 
                                     {/* </li> </ul> */}
                                </div>

                             );
                     }) }
             </div>
             </div>

            );


};

export default About;
