import React, { useState, useEffect } from "react";
import "../App.css";
import { db } from "../firebase";

const Firstform = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [date, setDate] = useState("");


  const [loader, setLoader] = useState(false);


  class Firstform extends React.Component {

  handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);
    const isValid = this.validate();

    if (isValid) {
    db.collection("Sujith")
      .add({
        name: name,
        email: email,
        phone: phone,
        date: date,
      })
      .then(() => {
        setLoader(false);
        alert("Your message has been submitted!");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });

    setName("");
    setEmail("");
    setPhone("");
    setDate("");
  }
};


handleChange = event => {
  const isCheckbox = event.target.type === "checkbox";
  this.setState({
    [event.target.name]: isCheckbox
      ? event.target.checked
      : event.target.value
  });
};

  validate = () => {
    let nameError = "";
    let emailError = "";


    if (!this.state.name) {
      nameError = "name cannot be blank";
    }

    if (!this.state.email.includes("@")) {
      emailError = "invalid email";
    }

    if (emailError || nameError) {
      this.setState({ emailError, nameError });
      return false;
    }

    return true;
  };
  }
  return (
    <div className = "row">
    <form className="form col-10 mx-auto justify-content-center" onSubmit={handleSubmit}>
      <h1 className="center">Hire Sujith! </h1>

      <label className = "labeltext">Name</label>
      <input
        placeholder="Name"
        value={name}
        onChange={this.handleChange}
      />

      <div style={{ fontSize: 12, color: "red" }}>
            {this.state.nameError}
          </div>

      <label className = "labeltext">Email</label>
      <input
        type = "email"
        placeholder="Email"
        value={email}
        onChange={this.handleChange}
      />

        <div style={{ fontSize: 12, color: "red" }}>
            {this.state.emailError}
          </div>

      <label className = "labeltext">Phone</label>
      <input
        placeholder="Phone"
        value={phone}
        onChange={this.handleChange}
      />

      <label className = "labeltext">Date</label>
      <input
        type = "date"
        placeholder="Date"
        value={date}
        onChange={this.handleChange}
      />

      <button className = "btn1 btn-primary btn-lg" type="submit">
        Submit
      </button>
    </form>
    </div>
  );
};


export default Firstform
