import { MutableRefObject, useRef, useEffect } from "react";

export default <T>(val: T): MutableRefObject<T> => {
  const ref = useRef(val);

  useEffect(() => {
    ref.current = val;
  });

  return ref;
};
import { Map } from "../types";

export default (arr: any[]): Map =>
  arr.reduce((obj, key) => {
    obj[key] = true;
    return obj;
  }, {});
export default (value: unknown): value is Object =>
  value !== null && typeof value === "object";
export default (value: unknown): value is FileList => value instanceof FileList;
import isObject from "./isObject";
import isFileList from "./isFileList";

const cloneObject = (object: unknown): any => {
  if (object instanceof Event) throw new Error("Unable to clone event.");

  if (!isObject(object) || isFileList(object)) return object;

  if (object instanceof Date) return new Date(object.getTime());

  if (Array.isArray(object)) return object.map((val) => cloneObject(val));

  if (isObject(object))
    return Object.keys(object).reduce((obj: Record<string, any>, key) => {
      obj[key] = cloneObject((object as Record<string, any>)[key]);
      return obj;
    }, {});

  throw new Error("Unable to clone object.");
};

export default cloneObject;
export default (arr: any[]): any[] => arr.filter(Boolean);
import isObject from "./isObject";

export default (value: unknown): value is Object =>
  !Array.isArray(value) && !(value instanceof Date) && isObject(value);
import isPlainObject from "./isPlainObject";

const deepMerge = (...objects: any[]): any =>
  objects.reduce((prev, obj) => {
    Object.keys(obj).forEach((key) => {
      const prevValue = prev[key];
      const currValue = obj[key];

      if (Array.isArray(prevValue) && Array.isArray(currValue)) {
        prev[key] = [...prevValue, ...currValue];
      } else if (isPlainObject(prevValue) && isPlainObject(currValue)) {
        prev[key] = deepMerge(prevValue, currValue);
      } else {
        prev[key] = currValue;
      }
    });

    return prev;
  }, {});

export default deepMerge;
export default (value: unknown): value is undefined => value === undefined;
import isPlainObject from "./isPlainObject";
import isUndefined from "./isUndefined";

const filterErrors = (error: unknown, touched: unknown): any => {
  if (!isPlainObject(error)) return touched ? error : undefined;

  return Object.keys(error).reduce((obj: Record<string, any>, key) => {
    const nextErrors = filterErrors(
      (error as Record<string, any>)[key],
      (touched as Record<string, any>)[key] || false
    );

    if (!isUndefined(nextErrors)) {
      obj[key] = nextErrors;
    } else {
      delete obj[key];
    }

    return obj;
  }, {});
};

export default filterErrors;
import compact from "./compact";

export default (str: string): string[] => {
  if (typeof str !== "string") throw new TypeError("Expected a string.");
  if (!str.length) return [];

  return compact(str.split(/[.[\]]+/));
};
import isPlainObject from "./isPlainObject";
import isUndefined from "./isUndefined";
import stringToPath from "./stringToPath";

// eslint-disable-next-line @typescript-eslint/explicit-module-boundary-types
export default (object: any, path: string, defaultValue?: unknown): any => {
  if (!isPlainObject(object) || !path) return defaultValue;

  const value = stringToPath(path).reduce(
    (obj, key) => (obj || {})[key],
    object
  );

  return isUndefined(value) ? defaultValue : value;
};
import isPlainObject from "./isPlainObject";

const getIsDirty = (dirty: object): boolean => {
  const search = (dty: object, found: any[] = []) => {
    for (const val of Object.values(dty)) {
      if (val === true) {
        found.push(val);
        return found;
      }

      if (isPlainObject(val)) search(val, found);
    }

    return found;
  };

  return !!search(dirty).length;
};

export default getIsDirty;
export default (value: unknown): value is Function =>
  typeof value === "function";
import isFunction from "./isFunction";

export default (value: unknown): value is Promise<any> =>
  isFunction(value) && value.constructor.name === "AsyncFunction";
import { FieldElement } from "../types";

export default (field: FieldElement): field is HTMLInputElement =>
  field.type === "checkbox";
import isPlainObject from "./isPlainObject";

export default (value: unknown): value is Record<string, never> =>
  isPlainObject(value) && !Object.keys(value).length;
import { FieldElement } from "../types";

export default (element: HTMLElement): element is FieldElement =>
  /INPUT|TEXTAREA|SELECT/.test(element.tagName);
import { FieldElement } from "../types";

export default (field: FieldElement): field is HTMLInputElement =>
  field.type === "file";
export default (element: HTMLElement): element is HTMLInputElement =>
  element.tagName === "INPUT";
import { FieldElement } from "../types";

export default (field: FieldElement): field is HTMLSelectElement =>
  field.type === "select-multiple";
import { FieldElement } from "../types";

export default (field: FieldElement): field is HTMLInputElement =>
  field.type === "number";
import { FieldElement } from "../types";

export default (field: FieldElement): field is HTMLInputElement =>
  field.type === "radio";
import { FieldElement } from "../types";

export default (field: FieldElement): field is HTMLInputElement =>
  field.type === "range";
export default (callback: (args: any) => any): any =>
  (
    window.requestIdleCallback ||
    ((cb) => {
      const start = Date.now();
      return setTimeout(
        () =>
          cb({
            didTimeout: false,
            timeRemaining: () => Math.max(0, 50 - (Date.now() - start)),
          }),
        1
      );
    })
  )(callback, { timeout: 2000 });
import cloneObject from "./cloneObject";
import isPlainObject from "./isPlainObject";
import stringToPath from "./stringToPath";

export default (
  // eslint-disable-next-line @typescript-eslint/explicit-module-boundary-types
  object: any,
  path: string,
  value: unknown,
  immutable = false
): any => {
  if (!isPlainObject(object)) throw new TypeError("Expected an object.");

  const segs = stringToPath(path);
  const newObject = immutable ? cloneObject(object) : object;

  segs.slice(0, -1).reduce((obj, key, idx) => {
    const nextIsNumber = !Number.isNaN(Number(segs[idx + 1]));
    if (isPlainObject(obj[key]) && !nextIsNumber) return obj[key];
    obj[key] = nextIsNumber ? [] : {};
    return obj[key];
  }, newObject)[segs[segs.length - 1] || ""] = value;

  return newObject;
};
/* eslint-disable no-prototype-builtins */

import cloneObject from "./cloneObject";
import isPlainObject from "./isPlainObject";
import isObject from "./isObject";
import stringToPath from "./stringToPath";

// eslint-disable-next-line @typescript-eslint/explicit-module-boundary-types
export default (object: any, path: string, immutable = false): any => {
  if (!isPlainObject(object)) throw new TypeError("Expected an object.");

  const refObject = immutable ? cloneObject(object) : object;
  const newObject = refObject;

  if (newObject.hasOwnProperty(path)) {
    delete newObject[path];
    return refObject;
  }

  const segs = stringToPath(path);

  if (!segs.length) return refObject;

  const last = segs.pop() as string;
  const target = segs.reduce((obj, key) => (obj || {})[key], newObject);

  if (isObject(target) && target.hasOwnProperty(last)) delete target[last];

  return refObject;
};
export default (...args: any[]): void => {
  if (__DEV__) console.warn(...args);
};
import { useReducer, useRef, useCallback } from "react";
import { dequal } from "dequal/lite";

import {
  Debug,
  FormState,
  FormStateReturn,
  Map,
  SetStateRef,
  SetUsedStateRef,
} from "../types";
import useLatest from "./useLatest";
import { get, getIsDirty, isEmptyObject, set } from "../utils";

export default <V>(
  initialState: FormState<V>,
  onChange?: Debug<V>
): FormStateReturn<V> => {
  const [, forceUpdate] = useReducer((c) => c + 1, 0);
  const stateRef = useRef(initialState);
  const usedStateRef = useRef<Map>({});
  const onChangeRef = useLatest(onChange || (() => undefined));

  const setStateRef = useCallback<SetStateRef>(
    (path, value, { fieldPath, shouldUpdate = true } = {}) => {
      const key = path.split(".")[0];

      if (!key) {
        if (!dequal(stateRef.current, value)) {
          stateRef.current = value;
          forceUpdate();
          onChangeRef.current(stateRef.current);
        }

        return;
      }

      if (
        (path !== "values" && key === "values") ||
        !dequal(get(stateRef.current, path), value)
      ) {
        const state = set(stateRef.current, path, value, true);
        const {
          errors,
          dirty,
          isDirty: prevIsDirty,
          isValid: prevIsValid,
        } = state;
        let { submitCount: prevSubmitCount } = state;
        const isDirty = key === "dirty" ? getIsDirty(dirty) : prevIsDirty;
        const isValid = key === "errors" ? isEmptyObject(errors) : prevIsValid;
        const submitCount =
          key === "isSubmitting" && value
            ? (prevSubmitCount += 1)
            : prevSubmitCount;

        stateRef.current = { ...state, isDirty, isValid, submitCount };

        path = fieldPath || path;

        if (
          shouldUpdate &&
          (Object.keys(usedStateRef.current).some(
            (k) => path.startsWith(k) || k.startsWith(path)
          ) ||
            (usedStateRef.current.isDirty && isDirty !== prevIsDirty) ||
            (usedStateRef.current.isValid && isValid !== prevIsValid))
        ) {
          forceUpdate();
          onChangeRef.current(stateRef.current);
        }
      }
    },
    [onChangeRef]
  );

  const setUsedStateRef = useCallback<SetUsedStateRef>(
    (path, unset = false) => {
      if (unset) {
        delete usedStateRef.current[path];
      } else {
        usedStateRef.current[path] = true;
      }
    },
    []
  );

  return { stateRef, setStateRef, setUsedStateRef };
};
import { useEffect, useLayoutEffect } from "react";

const useIsoLayoutEffect =
  typeof window === "undefined" ? useEffect : useLayoutEffect;

export default useIsoLayoutEffect;
/* eslint-disable @typescript-eslint/no-use-before-define */

import { useCallback, useEffect, useRef } from "react";

import {
  ClearErrors,
  Config,
  Controller,
  FieldArgs,
  FieldElement,
  FieldRef,
  Fields,
  FieldValidator,
  FieldsValue,
  FormErrors,
  FormState,
  FormValues,
  GetState,
  Map,
  Reset,
  Return,
  RunValidation,
  SetDirty,
  SetError,
  SetTouched,
  SetValue,
  Submit,
} from "./types";
import { useIsoLayoutEffect, useLatest, useState } from "./hooks";
import {
  arrayToMap,
  compact,
  deepMerge,
  filterErrors,
  get,
  isAsyncFunction,
  isCheckboxInput,
  isEmptyObject,
  isFieldElement,
  isFileInput,
  isFileList,
  isFunction,
  isInputElement,
  isMultipleSelect,
  isNumberInput,
  isPlainObject,
  isRadioInput,
  isRangeInput,
  isUndefined,
  runWithLowPriority,
  set,
  unset,
  warn,
} from "./utils";

export default <V extends FormValues = FormValues>({
