import React, { useState} from "react";
import ProfileCard from './ProfileCard';
import photographer from '../images/photographer.jpg'
import javadev from '../images/java.jpg'

const About = () => {

  const [input, setInput] = useState("");
  let profile = [
    { name: "Sujith UP", profession: "photographer", NavLink: "/first", image: {photographer} },
    { name: "Satish", profession: "python", image: {javadev} },
    { name: "Nagraj", profession: "java", image: {javadev} },
  ];

  const handleChange = (e) =>
  {
      e.preventDefault();
      setInput(e.target.value);
  };

  if(input.length > 0){
      profile = profile.filter((i) => {
          return i.name.toLowerCase().match(input);
      });
  }

    return(
            <div className= "row">
                <div class="column col-md-4">
                <input type="text" className = "form-control mr-auto"
                placeholder = "Search"
                onChange = {handleChange}
                value = {input} />

                {
                    profile.map((player, index) =>
                    {
                        return(
                                <div key={index}>
                                    {/* <ul> <li> */}
                                    <ProfileCard
                                    imgsrc = {javadev}
                                    name = {player.name}
                                    profession = {player.profession}
                                />
                                     - 
                                     {/* </li> </ul> */}
                                </div>
                             );
                     }) }
             </div>
             </div>
            );


};
export default About;
