import React, { useState} from "react";
import ProfileCard from './ProfileCard';
import photographer from '../images/photographer.jpg'
import javadev from '../images/java.jpg'

const Explore = () => {

  const [input, setInput] = useState("");
  let profile = [
    { name: "Sujith UP", profession: "Photographer and Editor", image: {photographer}, NavLink: "/first" },
    { name: "Satish NS", profession: "Python Developer", image: {javadev},NavLink: "/first" },
    { name: "Nagraj K", profession: "Java Developer", image: {javadev}, NavLink: "/first" },
    { name: "Girish M", profession: "Game Developer", image: {javadev}, NavLink: "/first" },
    { name: "Shashank N", profession: "Marketing Executive", image: {javadev}, NavLink: "/first" },
    { name: "Krishna R", profession: "Digital Artist", image: {javadev}, NavLink: "/first" },
    { name: "Arjun SN", profession: "Flutter App Developer", image: {javadev}, NavLink: "/first" },
    { name: "Harish Gowda", profession: "Ebiz", image: {javadev}, NavLink: "/first" },
    { name: "Rahul V", profession: "Frontend Developer", image: {javadev}, NavLink: "/first" },
    { name: "Manoj MU", profession: "Masterchef Cook", image: {javadev}, NavLink: "/first" },
  ];

  const handleChange = (e) =>
  {
      e.preventDefault();
      setInput(e.target.value);

  };

    if(input.length > 0){
      profile = profile.filter((i) => {
          return i.profession.toLowerCase().match(input);
      });

    }

    return(
            <div className= "row ">
                <div class="column ">

                <input type="text" className = "form-control col-md-3 mx-auto"
                placeholder = "Search"
                onChange = {handleChange}
                value = {input} />


                {
                    profile.map((player, index) =>
                    {
                        return(
                        //    <div className="container-fluid d-flex justify-content-center">
                                <div className="container-fluid column col-md-4" key={index}>
                                    {/* <ul> <li> */}
                                    <ProfileCard
                                    imgsrc = {player.image}
                                    name = {player.name}
                                    profession = {player.profession}
                                    NavLink = {player.NavLink}
                                />
                                     - 
                                     {/* </li> </ul> */}
                                </div>

                             );
                     }) }
             </div>
             </div>

            );


};

export default Explore;
