import React, { useState} from "react";
import ProfileCard from './ProfileCard';
import photographer from '../images/photographer.jpg'
import javadev from '../images/java.jpg'

const About = () => {

  const [input, setInput] = useState("");
  let profile = [
    { name: "Sujith UP", profession: "photographer", NavLink: "/first", imgsrc: {photographer} },
    { name: "Satish", profession: "python", imgsrc: {javadev} },
    { name: "Nagraj", profession: "java" },
  ];

  const handleChange = (e) =>
  {
      e.preventDefault();
      setInput(e.target.value);
  };

  if(input.length > 0){
      profile = profile.filter((i) => {
          return i.name.match(input);
      });
  }

    return(
            <div>
                <input type="text" placeholder = "Search"
                onChange = {handleChange}
                value = {input} />

                {
                    profile.map((player, index) =>
                    {
                        return(
                                <div key={index}>
                                    {/* <ul> <li> */}
                                    <ProfileCard
                                    imgsrc = {player.imgsrc}
                                    name = {player.name}
                                    profession = {player.profession}
                                />
                                     - 
                                     {/* </li> </ul> */}
                                </div>
                             );
                     }) }
             </div>
            );


};
export default About;
