import React, { useState} from "react";

const About = () => {

  const [input, setInput] = useState("");
  let profile = [
    { name: "Sujith UP", profession: "photographer" },
    { name: "Satish", profession: "python" },
    { name: "Nagraj", profession: "java" },
  ];

  const handleChange = (e) =>
  {
      e.preventDefault();
      setInput(e.target.value);
  };

  if(input.length > 0){
      profile = profile.filter((i) => {
          return i.name.match(input);
      });
  }

    return(
            <div>
                <input type="text" placeholder = "Search"
                onChange = {handleChange}
                value = {input} />

                {
                    profile.map((player, index) =>
                    {
                        return(
                                <div key={index}>
                                    <ul> <li>
                                    {player.name} - {player.profession}
                                     </li> </ul>
                                </div>
                             );
                     }) }
             </div>
            );


};
export default About;
